import React, { useState, useEffect } from "react";

export default function App() {
  const [report, setReport] = useState("");
  const [result, setResult] = useState(null);
  const [history, setHistory] = useState([]);

  const backendBase = "http://127.0.0.1:8000";

  const handleProcess = async () => {
    try {
      const res = await fetch(`${backendBase}/process-report`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ report })
      });
      const data = await res.json();
      setResult(data);
      fetchHistory();
    } catch (err) {
      console.error(err);
      alert("Error contacting backend. Make sure backend is running on port 8000.");
    }
  };

  const fetchHistory = async () => {
    try {
      const res = await fetch(`${backendBase}/reports`);
      const data = await res.json();
      setHistory(data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleTranslate = async (text, lang) => {
    try {
      const res = await fetch(`${backendBase}/translate`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ text, lang })
      });
      const data = await res.json();
      alert(`Translation (${lang}): ${data.translation}`);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchHistory();
  }, []);

  return (
    <div className="p-6 max-w-5xl mx-auto font-sans bg-gray-50 min-h-screen">
      <h1 className="text-4xl font-bold mb-6 text-purple-700">Regulatory Report Assistant</h1>

      <textarea
        className="w-full p-4 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-purple-500 mb-4"
        rows={6}
        placeholder="Paste an adverse event report here..."
        value={report}
        onChange={(e) => setReport(e.target.value)}
      />

      <button
        onClick={handleProcess}
        className="bg-purple-600 text-white px-6 py-2 rounded-lg hover:bg-purple-700 transition mb-6"
      >
        Process Report
      </button>

      {result && (
        <div className="mt-6 p-6 bg-white rounded-lg shadow-md border border-gray-200">
          <h3 className="text-2xl font-semibold mb-4 text-gray-800">Result</h3>
          <p><strong>Drug:</strong> {result.drug}</p>
          <p><strong>Adverse Events:</strong> {result.adverse_events && result.adverse_events.join(", ")}</p>
          <p><strong>Severity:</strong> {result.severity}</p>
          <p><strong>Outcome:</strong> {result.outcome}</p>

          <div className="mt-4 flex gap-3">
            <button
              onClick={() => handleTranslate(result.outcome, "fr")}
              className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition"
            >
              Translate Outcome (FR)
            </button>
            <button
              onClick={() => handleTranslate(result.outcome, "sw")}
              className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition"
            >
              Translate Outcome (SW)
            </button>
          </div>
        </div>
      )}

      <div className="mt-10">
        <h3 className="text-2xl font-semibold mb-4 text-gray-800">History (latest 10)</h3>
        <div className="overflow-x-auto">
          <table className="min-w-full border border-gray-300 rounded-lg overflow-hidden">
            <thead className="bg-gray-200">
              <tr>
                <th className="p-2 border">ID</th>
                <th className="p-2 border">Drug</th>
                <th className="p-2 border">Events</th>
                <th className="p-2 border">Severity</th>
                <th className="p-2 border">Outcome</th>
                <th className="p-2 border">Created At</th>
              </tr>
            </thead>
            <tbody>
              {history.slice(0, 10).map((r) => (
                <tr key={r.id} className="hover:bg-gray-100">
                  <td className="p-2 border">{r.id}</td>
                  <td className="p-2 border">{r.drug}</td>
                  <td className="p-2 border">{r.adverse_events && r.adverse_events.join(", ")}</td>
                  <td className="p-2 border">{r.severity}</td>
                  <td className="p-2 border">{r.outcome}</td>
                  <td className="p-2 border">{r.created_at}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
